// 函数调用表达式
const sayHi = () => 'Hi~'

// 标签表达式
const dv = <div>我是一个div</div>

const title = (
  <h1 className ='title'>
    Hello, {name} 
    <p>{1}</p>
    <p>{1 + 7}</p>
    <p>{10 > 5 ? '大于' : '小于'}</p>
    <p>{sayHi()}</p>
    <p>{dv}</p>
    <span />
  </h1>
)

const loading = true
const loadData = () => {
  if (loading) {
    return <div>loading...</div>
  }
  return <div>数据加载完成，此处系那是加载后的数据</div>
}

